INSERT INTO `recipes` (`id`, `title`, `description`, `video_link`, `img`) VALUES
(1, 'Fried Potatoes With Mushrooms', '<h2>Fried Potatoes with Mushrooms</h2>\r\n  <h3>Ingredients</h3>\r\n  <ul>\r\n    <li>4 medium potatoes, peeled and sliced</li>\r\n    <li>200–300g mushrooms, cleaned and sliced</li>\r\n    <li>1 medium onion, thinly sliced</li>\r\n    <li>2 garlic cloves, minced (optional)</li>\r\n    <li>2 tbsp butter (optional)</li>\r\n    <li>2–3 tbsp oil (vegetable or olive)</li>\r\n    <li>Salt, to taste</li>\r\n    <li>Black pepper, to taste</li>\r\n    <li>Fresh herbs (dill, parsley, or chives) for garnish</li>\r\n  </ul>\r\n  <h3>Instructions</h3>\r\n  <ol>\r\n    <li>Heat oil in a large skillet over medium heat.</li>\r\n    <li>Add the sliced potatoes, spread them evenly, and fry until golden on one side.</li>\r\n    <li>Flip the potatoes and add the sliced onions. Fry for another 5 minutes.</li>\r\n    <li>Add the mushrooms and garlic (if using). Stir occasionally and cook until mushrooms are tender.</li>\r\n    <li>Season with salt and black pepper to taste.</li>\r\n    <li>Optional: Add butter for extra flavor and stir well.</li>\r\n    <li>Garnish with fresh herbs before serving.</li>\r\n  </ol>', 'https://www.youtube.com/embed/heuImM1dzTc', 'Fried-Potatoes-With-Mushrooms.avif'),
(2, 'Greek Salad', '<h2>Greek Salad</h2>\r\n  <h3>Ingredients</h3>\r\n  <ul>\r\n    <li>2 large tomatoes, chopped</li>\r\n    <li>1 cucumber, sliced into half-moons</li>\r\n    <li>1 red onion, thinly sliced</li>\r\n    <li>1 green bell pepper, sliced</li>\r\n    <li>100g feta cheese, cubed or crumbled</li>\r\n    <li>1/2 cup Kalamata olives</li>\r\n    <li>3–4 tbsp olive oil</li>\r\n    <li>1 tbsp red wine vinegar</li>\r\n    <li>1 tsp dried oregano</li>\r\n    <li>Salt and black pepper, to taste</li>\r\n  </ul>\r\n  <h3>Instructions</h3>\r\n  <ol>\r\n    <li>In a large bowl, combine tomatoes, cucumber, red onion, and green bell pepper.</li>\r\n    <li>Add the Kalamata olives and feta cheese.</li>\r\n    <li>Drizzle with olive oil and red wine vinegar.</li>\r\n    <li>Sprinkle with dried oregano, salt, and black pepper to taste.</li>\r\n    <li>Toss gently to combine all ingredients.</li>\r\n    <li>Serve immediately or chill briefly before serving.</li>\r\n  </ol>', 'https://www.youtube.com/embed/Pc1OORZb57g?si=agYT3gSoSG_gpZa6', 'Greek-Salad.avif'),
(3, 'Perfect Beef Steak AAA', '<h2>Perfect Beef Steak AAA</h2>\r\n  <h3>Ingredients</h3>\r\n  <ul>\r\n    <li>1 AAA-grade beef steak (ribeye, sirloin, or tenderloin), about 1-inch thick</li>\r\n    <li>2 tbsp olive oil</li>\r\n    <li>2 cloves garlic, smashed</li>\r\n    <li>2 sprigs fresh rosemary or thyme</li>\r\n    <li>1 tbsp butter</li>\r\n    <li>Salt, to taste</li>\r\n    <li>Freshly ground black pepper, to taste</li>\r\n  </ul>\r\n  <h3>Instructions</h3>\r\n  <ol>\r\n    <li>Remove the steak from the fridge and let it come to room temperature (about 30 minutes).</li>\r\n    <li>Season the steak generously with salt and freshly ground black pepper on both sides.</li>\r\n    <li>Heat a skillet or cast-iron pan over high heat and add the olive oil.</li>\r\n    <li>Once the oil is hot, place the steak in the pan and sear for 2–3 minutes on one side until a golden crust forms.</li>\r\n    <li>Flip the steak, add butter, garlic, and rosemary/thyme to the pan.</li>\r\n    <li>While searing the other side, baste the steak by spooning the melted butter over it for about 2 minutes.</li>\r\n    <li>Remove the steak from the pan and let it rest for 5–7 minutes to allow juices to redistribute.</li>\r\n    <li>Slice and serve with your favorite side dishes.</li>\r\n  </ol>', 'https://www.youtube.com/embed/3dEEylRXG9c', 'Perfect-Beef-Steak-AAA.avif'),
(4, 'French Rabbit In The Oven', '<h2>French Rabbit in the Oven</h2>\r\n  <h3>Ingredients</h3>\r\n  <ul>\r\n    <li>1 whole rabbit, cut into pieces</li>\r\n    <li>2 tbsp olive oil</li>\r\n    <li>2 tbsp butter</li>\r\n    <li>1 onion, finely chopped</li>\r\n    <li>4 garlic cloves, minced</li>\r\n    <li>1 cup dry white wine</li>\r\n    <li>1 cup chicken or vegetable broth</li>\r\n    <li>2 sprigs fresh thyme</li>\r\n    <li>2 bay leaves</li>\r\n    <li>Salt and freshly ground black pepper, to taste</li>\r\n    <li>1/2 cup heavy cream (optional, for a creamy version)</li>\r\n  </ul>\r\n  <h3>Instructions</h3>\r\n  <ol>\r\n    <li>Preheat the oven to 180°C (350°F).</li>\r\n    <li>Season the rabbit pieces with salt and black pepper.</li>\r\n    <li>In a large oven-safe skillet or Dutch oven, heat olive oil and butter over medium-high heat.</li>\r\n    <li>Sear the rabbit pieces on all sides until golden brown, then remove and set aside.</li>\r\n    <li>In the same pan, sauté the onion and garlic until softened and fragrant.</li>\r\n    <li>Deglaze the pan with white wine, scraping up any browned bits, and let it reduce slightly.</li>\r\n    <li>Return the rabbit to the pan, add the broth, thyme, and bay leaves.</li>\r\n    <li>Cover the skillet with a lid or foil and place it in the oven.</li>\r\n    <li>Bake for 45–60 minutes, or until the rabbit is tender and fully cooked.</li>\r\n    <li>Optional: Stir in heavy cream during the last 10 minutes for a creamy sauce.</li>\r\n    <li>Remove from the oven, discard the thyme and bay leaves, and serve hot.</li>\r\n  </ol>', 'https://www.youtube.com/embed/u9sU43GnYrg?si=loY4SrdpceG1b6tl', 'French-Rabbit-In-The-Oven.avif'),
(5, 'Chicken Sandwich', '<h2>Vietnamese Chicken Sandwich (Bánh Mì Gà)</h2>\r\n  <h3>Ingredients</h3>\r\n  <ul>\r\n    <li>1 baguette (crusty, Vietnamese-style preferred)</li>\r\n    <li>1 cooked chicken breast, shredded or thinly sliced</li>\r\n    <li>1 small carrot, julienned</li>\r\n    <li>1/2 cucumber, thinly sliced</li>\r\n    <li>1/2 cup daikon radish, julienned (optional)</li>\r\n    <li>2 tbsp mayonnaise</li>\r\n    <li>1 tsp soy sauce</li>\r\n    <li>Fresh cilantro leaves</li>\r\n    <li>1 jalapeño or chili pepper, thinly sliced (optional)</li>\r\n    <li>1 tsp rice vinegar</li>\r\n    <li>1 tsp sugar</li>\r\n    <li>Salt, to taste</li>\r\n  </ul>\r\n  <h3>Instructions</h3>\r\n  <ol>\r\n    <li>In a small bowl, mix the carrot, daikon radish, rice vinegar, sugar, and a pinch of salt. Let it sit for 10 minutes to pickle slightly.</li>\r\n    <li>Toast the baguette until warm and crispy, then slice it lengthwise.</li>\r\n    <li>Spread mayonnaise on the inside of the baguette and drizzle with a little soy sauce.</li>\r\n    <li>Layer the shredded chicken, pickled carrot and radish, cucumber slices, and cilantro leaves.</li>\r\n    <li>Add jalapeño slices for extra spice, if desired.</li>\r\n    <li>Close the sandwich, press gently, and serve immediately.</li>\r\n  </ol>', 'https://www.youtube.com/embed/Wk1tKlgMheQ?si=S-5PLBBDwgeOhdXN', 'Chicken-Sandwich.avif'),
(6, 'Russian Borscht Soup', '<h2>Russian Borscht Soup</h2>\r\n  <h3>Ingredients</h3>\r\n  <ul>\r\n    <li>1 lb (450g) beef or pork (with bone, for broth)</li>\r\n    <li>6 cups water</li>\r\n    <li>2 medium beets, peeled and grated</li>\r\n    <li>2 medium potatoes, peeled and diced</li>\r\n    <li>1 carrot, peeled and grated</li>\r\n    <li>1 onion, finely chopped</li>\r\n    <li>1/4 head of cabbage, shredded</li>\r\n    <li>2 tbsp tomato paste</li>\r\n    <li>2 tbsp vegetable oil</li>\r\n    <li>2 cloves garlic, minced</li>\r\n    <li>1 tbsp vinegar (white or apple cider)</li>\r\n    <li>1 tbsp sugar</li>\r\n    <li>Salt and black pepper, to taste</li>\r\n    <li>Fresh dill or parsley, chopped (for garnish)</li>\r\n    <li>Sour cream (for serving)</li>\r\n  </ul>\r\n  <h3>Instructions</h3>\r\n  <ol>\r\n    <li>In a large pot, add the beef or pork and water. Bring to a boil, skim off the foam, reduce heat, and simmer for 1–1.5 hours to create a flavorful broth.</li>\r\n    <li>Remove the meat, shred it, and set aside. Strain the broth if needed and return it to the pot.</li>\r\n    <li>Heat vegetable oil in a skillet over medium heat. Sauté the grated beets for 3–4 minutes.</li>\r\n    <li>Add vinegar, sugar, and tomato paste to the beets. Stir well and cook for another 5 minutes. Set aside.</li>\r\n    <li>To the pot of broth, add diced potatoes and cook for 10 minutes.</li>\r\n    <li>Add the shredded cabbage, grated carrot, and chopped onion. Cook for another 10 minutes.</li>\r\n    <li>Stir in the sautéed beets and minced garlic. Season with salt and black pepper to taste.</li>\r\n    <li>Simmer the soup for 10–15 minutes until all vegetables are tender.</li>\r\n    <li>Return the shredded meat to the pot and let it warm through.</li>\r\n    <li>Serve hot, garnished with fresh dill or parsley and a dollop of sour cream.</li>\r\n  </ol>', 'https://www.youtube.com/embed/qRospcusjCE?si=19BoDACgnQhu_kqR', 'Russian-Borscht-Soup.avif');

